import React, { useState } from 'react';
import InputField from './InputField';
import Button from './Button';
import './styles/LoginForm.css';
import illustrator from './assets/illustrator.png';

const LoginForm = () => {
  // ... (state dan fungsi handleSubmit) ...

  return (
    <div className="loginContainer">
      <div className="loginForm">
        <div className="formContainer"> {/* Tambahkan container untuk formulir */}
          <div className="loginForm"> {/* Formulir di dalam container */}
            <h1>SIMS PPOB</h1>
            <p>Masuk atau buat akun untuk memulai</p>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleSubmit}>
              <InputField label="Email" type="email" placeholder="masukkan email anda" value={email} onChange={(e) => setEmail(e.target.value)} />
              <InputField label="Password" type="password" placeholder="masukkan password anda" value={password} onChange={(e) => setPassword(e.target.value)} />
              <Button text="Masuk" />
              <p>Belum punya akun? <a href="#">Registrasi di sini</a></p>
            </form>
          </div>
        </div>
        <div className="illustrationContainer"> {/* Container untuk ilustrasi */}
          <img src={illustrator} alt="Illustration" className="illustration" />
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
