import React, { useState } from 'react';
import InputField from './InputField';
import './styles/RegisterForm.css';
import illustration from './assets/illustrator.png';

const RegisterForm = () => {
  // ... state variables ...

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    if (password !== confirmPassword) {
      setError('Password dan konfirmasi password tidak cocok.');
      return;
    }

    console.log('Form Submitted:', { firstName, lastName, email, password });
  };

  return (
    <div className="registerContainer">
      <div className="formContainer">
        <h1>SIMS PPOB</h1>
        <p>Lengkap data untuk membuat akun</p>
        {error && <p className="error">{error}</p>}
        <form onSubmit={handleSubmit}>
          <InputField label="Email" type="email" placeholder="Email" value={email} onChange={setEmail} />
          <InputField label="Nama Depan" type="text" placeholder="Nama Depan" value={firstName} onChange={setFirstName} />
          <InputField label="Nama Belakang" type="text" placeholder="Nama Belakang" value={lastName} onChange={setLastName} />
          <InputField label="Buat Password" type="password" placeholder="Password" value={password} onChange={setPassword} />
          <InputField label="Konfirmasi Password" type="password" placeholder="Konfirmasi Password" value={confirmPassword} onChange={setConfirmPassword} />
          <button type="submit" className="button-blue">Registrasi</button>
        </form>
        <p>Sudah punya akun? <a href="/">Login di sini</a></p>
      </div>
      <div className="illustrationContainer">
        <img src={illustration} alt="Illustration" className="illustration" />
      </div>
    </div>
  );
};

export default RegisterForm;
