import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/TopUpPage.css';

const TopUpPage = () => {
  const [topUpAmount, setTopUpAmount] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleTopUpAmountChange = (e) => {
    setTopUpAmount(e.target.value);
    setError(''); // Clear error when typing
  };

  const handlePredefinedAmountClick = (amount) => {
    setTopUpAmount(amount);
    setError(''); // Clear error when selecting predefined amount
  };

  const handleTopUpSubmit = () => {
    if (!topUpAmount || topUpAmount <= 0) {
      setError('Nominal top up harus lebih dari 0');
      return;
    }

    // Simulasi redirect setelah top-up berhasil
    alert(`Top Up sebesar Rp${topUpAmount.toLocaleString()} berhasil!`);
    navigate('/transaction'); // Redirect ke halaman lain (misalnya ke Transaction)
  };

  return (
    <div className="top-up-page">
      <header>
        <h1>SIMS PPOB</h1>
      </header>
      <div className="balance-card">
        <p>Saldo anda</p>
        <h2>Rp 0</h2>
        <button className="hide-balance">Tutup Saldo üëÅ</button>
      </div>
      <h2>Selamat datang, Kristanto Wibowo</h2>
      <p>Silahkan masukkan</p>
      <h3>Nominal Top Up</h3>
      {error && <p className="error-message">{error}</p>}
      <div className="top-up-form">
        <div className="input-group">
          <input
            type="number"
            placeholder="Masukkan nominal Top Up"
            value={topUpAmount}
            onChange={handleTopUpAmountChange}
          />
          <button className="top-up-button" onClick={handleTopUpSubmit}>Top Up</button>
        </div>
        <div className="predefined-amounts">
          <button onClick={() => handlePredefinedAmountClick(10000)}>Rp10.000</button>
          <button onClick={() => handlePredefinedAmountClick(20000)}>Rp20.000</button>
          <button onClick={() => handlePredefinedAmountClick(50000)}>Rp50.000</button>
          <button onClick={() => handlePredefinedAmountClick(100000)}>Rp100.000</button>
          <button onClick={() => handlePredefinedAmountClick(250000)}>Rp250.000</button>
          <button onClick={() => handlePredefinedAmountClick(500000)}>Rp500.000</button>
        </div>
      </div>
    </div>
  );
};

export default TopUpPage;
