import React, { useState } from 'react';
import InputField from './InputField';
import Button from './Button';
import './components/styles/LoginForm.css';
import './assets/illustrator.png';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // Untuk menampilkan pesan error

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Bersihkan pesan error sebelumnya

    try {
      const response = await fetch('/api/login', { // Ganti dengan endpoint API Anda
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.message || 'Login gagal.');
      } else {
        const data = await response.json();
        // Lakukan tindakan setelah login berhasil (misalnya, redirect)
        console.log('Login berhasil:', data);
      }
    } catch (error) {
      setError('Terjadi kesalahan. Silakan coba lagi.');
      console.error('Error:', error);
    }
  };

  return (
    <div className="loginContainer">
      <div className={styles.loginContainer}>
        <div className={styles.loginForm}>
          <img src="/path/to/your/illustration.svg" alt="Illustration" className={styles.illustration} /> {/* Ganti dengan path gambar ilustrasi */}
          <h1>SIMS PPOB</h1>
          <p>Masuk atau buat akun untuk memulai</p>
          {error && <p className={styles.error}>{error}</p>} {/* Tampilkan pesan error */}
          <form onSubmit={handleSubmit}>
            <InputField label="Email" type="email" placeholder="masukkan email anda" value={email} onChange={(e) => setEmail(e.target.value)} />
            <InputField label="Password" type="password" placeholder="masukkan password anda" value={password} onChange={(e) => setPassword(e.target.value)} />
            <Button text="Masuk" />
            <p>Belum punya akun? <a href="#">Registrasi di sini</a></p>
          </form>
        </div> {/* Pastikan ini untuk menutup div loginForm */}
      </div> {/* Pastikan ini untuk menutup div loginContainer */}
    </div> // Menutup div loginContainer yang pertama
  );
  
  
};

export default LoginForm;
