// Store.js
import { configureStore } from '@reduxjs/toolkit';
import authReducer from './authSlice'; // Pastikan path-nya sesuai

const store = configureStore({
  reducer: {
    auth: authReducer, // Menambahkan authReducer ke store
  },
});

export default store;
