// redux/authSlice.js
const initialState = {
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
  };
  
  const authSlice = (state = initialState, action) => {
    switch (action.type) {
      case 'SET_EMAIL':
        return { ...state, email: action.payload };
      case 'SET_PASSWORD':
        return { ...state, password: action.payload };
      case 'SET_FIRST_NAME':
        return { ...state, firstName: action.payload };
      case 'SET_LAST_NAME':
        return { ...state, lastName: action.payload };
      case 'SET_CONFIRM_PASSWORD':
        return { ...state, confirmPassword: action.payload };
      default:
        return state;
    }
  };
  
  export const setEmail = (email) => ({ type: 'SET_EMAIL', payload: email });
  export const setPassword = (password) => ({ type: 'SET_PASSWORD', payload: password });
  export const setFirstName = (firstName) => ({ type: 'SET_FIRST_NAME', payload: firstName });
  export const setLastName = (lastName) => ({ type: 'SET_LAST_NAME', payload: lastName });
  export const setConfirmPassword = (confirmPassword) => ({ type: 'SET_CONFIRM_PASSWORD', payload: confirmPassword });
  
  export default authSlice;
