import { configureStore } from '@reduxjs/toolkit';
import profileReducer from './profileSlice'; // Make sure the path to your profile slice is correct
import balanceReducer from './balanceSlice'; // Make sure the path to your balance slice is correct
import servicesReducer from './servicesSlice'; // Ensure the path to your services slice is correct
import bannerReducer from './bannerSlice'; // Ensure the path to your banner slice is correct
import errorReducer from './errorSlice'; // Ensure the path to your error slice is correct

const store = configureStore({
  reducer: {
    profile: profileReducer, // This will manage state for profile data
    balance: balanceReducer, // This will manage state for balance data
    services: servicesReducer, // This will manage state for services data
    banner: bannerReducer, // This will manage state for banner data
    error: errorReducer, // This will manage state for error handling
  },
});

export default store;
