import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/HomePage.css';
import ProfilePhoto from '../assets/ProfilePhoto.png'; // Fallback image

const HomePage = () => {
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [dropdownVisible, setDropdownVisible] = useState(false); // State for dropdown visibility
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Token tidak ditemukan!');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch('https://take-home-test-api.nutech-integrasi.com/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'accept': 'application/json',
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          setError(`Terjadi kesalahan: ${errorData.message || 'Unknown error'}`);
        } else {
          const data = await response.json();
          setUserData(data);
        }
      } catch (error) {
        setError('Terjadi kesalahan saat mengambil data user.');
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  // Toggle the dropdown visibility
  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);
  };

  // Logout function
  const logout = () => {
    localStorage.removeItem('token');
    navigate('/login'); // Navigate to login page after logout
  };

  // Handle profile update
  const updateProfile = () => {
    navigate('/update-profile'); // Redirect to the profile update page
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!userData) {
    return <div>Data user tidak tersedia.</div>;
  }

  return (
    <div className="homepage">
      <header>
        <h1>SIMS PPOB</h1>
        <nav>
          <ul>
            <li>Top Up</li>
            <li>Transaction</li>
            <li onClick={toggleDropdown} className="account">
              Akun
              {dropdownVisible && (
                <ul className="dropdown">
                  <li onClick={updateProfile}>Update Profil</li>
                  <li onClick={logout}>Logout</li>
                </ul>
              )}
            </li>
          </ul>
        </nav>
      </header>

      <main>
        <section className="user-info">
          <img src={userData.profileImage ? userData.profileImage : ProfilePhoto} alt="User Avatar" className="avatar" />
          <p>Selamat datang, {userData.name}</p>
        </section>

        {/* Other sections of the homepage */}
      </main>

      <footer>
        {/* Footer content here */}
      </footer>
    </div>
  );
};

export default HomePage;
