import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/TopUpPage.css';


const TopUpPage = () => {
  // ... (state variables and functions remain the same) ...

  return (
    <div className="top-up-page">
      <h1>Top Up</h1>
      {error && <p className="error-message">{error}</p>}
      <div className="top-up-form">
        <div className="input-group">
          <input
            type="number"
            placeholder="Masukkan nominal Top Up"
            value={topUpAmount}
            onChange={handleTopUpAmountChange}
          />
          <button className="top-up-button" onClick={handleTopUpSubmit}>Top Up</button>
        </div>
        <div className="predefined-amounts">
          <div className="amount-row">
            <button onClick={() => handlePredefinedAmountClick(10000)}>Rp10.000</button>
            <button onClick={() => handlePredefinedAmountClick(20000)}>Rp20.000</button>
            <button onClick={() => handlePredefinedAmountClick(50000)}>Rp50.000</button>
          </div>
          <div className="amount-row">
            <button onClick={() => handlePredefinedAmountClick(100000)}>Rp100.000</button>
            <button onClick={() => handlePredefinedAmountClick(250000)}>Rp250.000</button>
            <button onClick={() => handlePredefinedAmountClick(500000)}>Rp500.000</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TopUpPage;
