import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./styles/TopUpPage.css";

const TopUpPage = () => {
  const [topUpAmount, setTopUpAmount] = useState("");
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const token = "YOUR_JWT_TOKEN_HERE"; // Ganti dengan token JWT sebenarnya setelah login

  const handleTopUpAmountChange = (e) => {
    setTopUpAmount(e.target.value);
    setError("");
    setSuccessMessage("");
  };

  const handlePredefinedAmountClick = (amount) => {
    setTopUpAmount(amount);
    setError("");
    setSuccessMessage("");
  };

  const handleTopUpSubmit = async () => {
    if (!topUpAmount || Number(topUpAmount) <= 0) {
      setError("Nominal top up harus lebih dari 0");
      return;
    }

    try {
      setLoading(true);
      setError("");
      setSuccessMessage("");

      const response = await axios.post(
        "https://take-home-test-api.nutech-integrasi.com/topup",
        { top_up_amount: Number(topUpAmount) },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setSuccessMessage(
        `Top Up berhasil! Saldo Anda bertambah sebesar Rp${Number(
          topUpAmount
        ).toLocaleString()}.`
      );
      setTopUpAmount("");
      navigate("/transaction"); // Navigasi ke halaman transaksi
    } catch (err) {
      if (err.response) {
        setError(
          `Top Up gagal: ${err.response.data.message || "Terjadi kesalahan"}`
        );
      } else {
        setError("Top Up gagal: Tidak dapat terhubung ke server.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="top-up-container">
      <header className="top-up-header">
        <h1>SIMS PPOB</h1>
        <nav>
          <a href="#">Top Up</a>
          <a href="#">Transaction</a>
          <a href="#">Akun</a>
        </nav>
      </header>

      <div className="top-up-content">
        <div className="top-up-balance">
          <h2>Saldo anda</h2>
          <p className="balance-amount">Rp 0</p>
          <button className="hide-balance">Tutup Saldo üëÅ</button>
        </div>

        <div className="user-greeting">
          <h3>Selamat datang,</h3>
          <h2>Kristanto Wibowo</h2>
          <p>Silahkan masukkan</p>
        </div>

        <h3 className="top-up-title">Nominal Top Up</h3>
        {error && <p className="error-message">{error}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>}

        <div className="top-up-form">
          <input
            type="number"
            placeholder="Masukkan nominal Top Up"
            value={topUpAmount}
            onChange={handleTopUpAmountChange}
            disabled={loading}
          />
          <button
            className="top-up-button"
            onClick={handleTopUpSubmit}
            disabled={loading}
          >
            {loading ? "Proses..." : "Top Up"}
          </button>
        </div>

        <div className="predefined-amounts">
          <button
            onClick={() => handlePredefinedAmountClick(10000)}
            disabled={loading}
          >
            Rp10.000
          </button>
          <button
            onClick={() => handlePredefinedAmountClick(20000)}
            disabled={loading}
          >
            Rp20.000
          </button>
          <button
            onClick={() => handlePredefinedAmountClick(50000)}
            disabled={loading}
          >
            Rp50.000
          </button>
          <button
            onClick={() => handlePredefinedAmountClick(100000)}
            disabled={loading}
          >
            Rp100.000
          </button>
          <button
            onClick={() => handlePredefinedAmountClick(250000)}
            disabled={loading}
          >
            Rp250.000
          </button>
          <button
            onClick={() => handlePredefinedAmountClick(500000)}
            disabled={loading}
          >
            Rp500.000
          </button>
        </div>
      </div>
    </div>
  );
};

export default TopUpPage;
