import React, { useState } from 'react';
import InputField from './InputField';
import './styles/LoginForm.css';
import illustrator from './assets/illustrator.png';
import { Link, useNavigate } from 'react-router-dom';

const LoginForm = ({ setIsLoggedIn }) => {
  const [email, setEmail] = useState(''); // Declare email using useState
  const [password, setPassword] = useState(''); // Declare password using useState
  const [error, setError] = useState(''); // Declare error using useState
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Now setError is defined

    try {
      const response = await fetch('/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }), // email and password are now defined
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.message || 'Login gagal.');
      } else {
        const data = await response.json();
        console.log('Login berhasil:', data);
        setIsLoggedIn(true);
        navigate('/home');
      }
    } catch (error) {
      setError('Terjadi kesalahan. Silakan coba lagi.');
      console.error('Error:', error);
    }
  };

  // ... rest of your LoginForm component ...
};

export default LoginForm;
