import React from 'react';
import './styles/HomePage.css';
import { Card, CardContent } from "@/components/ui/card";

const ServiceItem = ({ name, icon }) => (
  <div className="flex flex-col items-center text-center p-4 bg-gray-100 rounded-full shadow">
    <span className="text-2xl">{icon}</span>
    <p className="mt-2 text-sm">{name}</p>
  </div>
);

const PromoItem = ({ title, description, bgColor }) => (
  <Card className={`p-4 ${bgColor} text-white`}>
    <CardContent>
      <h4 className="text-lg font-bold">{title}</h4>
      <p className="text-sm">{description}</p>
    </CardContent>
  </Card>
);

const HomePage = () => {
  const userName = "Kristanto Wibowo";
  const saldo = "Rp ********";

  const services = [
    // ... (data layanan) ...
  ];

  const promotions = [
    // ... (data promosi) ...
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="flex justify-between items-center px-4 py-3 bg-white shadow">
        <h1 className="text-lg font-bold">SIMS PPOB</h1>
        <nav className="flex gap-4">
          <a href="/top-up" className="text-sm text-gray-700">Top Up</a>
          <a href="/transaction" className="text-sm text-gray-700">Transaction</a>
          <a href="/account" className="text-sm text-gray-700">Akun</a>
        </nav>
      </header>

      <main className="px-4 py-6">
        <section className="mb-6"> {/* Section untuk salam dan saldo */}
          <h2 className="text-xl font-semibold">Selamat datang,</h2>
          <p className="text-2xl font-bold">{userName}</p>
          <div className="mt-4 bg-red-500 text-white p-4 rounded-lg shadow-lg">
            <p className="text-sm">Saldo Anda</p>
            <p className="text-3xl font-bold">{saldo}</p>
            <button className="mt-2 text-sm underline">Lihat Saldo</button>
          </div>
        </section>

        <section className="mb-8"> {/* Section untuk layanan */}
          <h3 className="text-lg font-semibold mb-4">Layanan</h3>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4"> {/* Grid responsif */}
            {services.map((service, index) => (
              <ServiceItem key={index} name={service.name} icon={service.icon} />
            ))}
          </div>
        </section>

        <section> {/* Section untuk promosi */}
          <h3 className="text-lg font-semibold mb-4">Temukan promo menarik</h3>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4"> {/* Grid responsif */}
            {promotions.map((promo, index) => (
              <PromoItem key={index} title={promo.title} description={promo.description} bgColor={promo.bgColor} />
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default HomePage;
