import React, { useEffect, useRef, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { setProfile, setBalance, setServices, setBanner, setError } from '../redux/actions';
import './styles/HomePage.css';
// ... (import komponen lainnya seperti ProfilePhoto, PBBIcon, dll.)

const HomePage = () => {
  const servicesListRef = useRef();
  const bannerSliderRef = useRef();
  const [currentBanner, setCurrentBanner] = useState(0);
  const [accountDropdownVisible, setAccountDropdownVisible] = useState(false); // State untuk dropdown akun

  const handleScroll = (e) => {
    // ... (fungsi handleScroll seperti sebelumnya)
  };

  const scrollRight = () => {
    // ... (fungsi scrollRight seperti sebelumnya)
  };

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const profile = useSelector((state) => state.profile);
  const balance = useSelector((state) => state.balance);
  const services = useSelector((state) => state.services);
  const bannerFromRedux = useSelector((state) => state.banner);
  const error = useSelector((state) => state.error);

  useEffect(() => {
    const fetchData = async () => {
      // ... (fetchData seperti sebelumnya)
    };
    fetchData();
  }, [dispatch]);

  const banner = bannerFromRedux || [];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentBanner((prevIndex) => (prevIndex + 1) % banner.length);
    }, 5000); // Ganti 5000 dengan durasi yang diinginkan (dalam ms)
    return () => clearInterval(interval);
  }, [banner.length]);

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!profile || !balance || !services || !bannerFromRedux) {
    return <div>Loading...</div>;
  }

  const servicesList = [
    // ... (daftar layanan seperti sebelumnya)
  ];

  const toggleAccountDropdown = () => {
    setAccountDropdownVisible(!accountDropdownVisible);
  };

  const handleTopUpClick = () => {
    navigate('/topup');
  };

  return (
    <div className="homepage">
      <header>
        <h1 className="logo">
          <Link to="/" style={{ textDecoration: 'none', color: 'black' }}>
            SIMS PPOB
          </Link>
        </h1>
        <nav>
          <ul>
            <li onClick={handleTopUpClick}>Top Up</li>
            <li onClick={() => navigate('/transaction')}>Transaction</li>
            <li className="account-btn" onClick={toggleAccountDropdown}>
              Akun
              {accountDropdownVisible && (
                <div className="account-dropdown">
                  {/* ... (Dropdown akun seperti sebelumnya) */}
                </div>
              )}
            </li>
          </ul>
        </nav>
      </header>

      <main>
        <section className="user-info">
          <img
            src={profile.profileImage || ProfilePhoto}
            alt="User Avatar"
            className="avatar"
          />
          <p>Selamat datang, {profile.name}</p>
        </section>

        <section className="saldo">
          <div className="saldo-container">
            <p>Saldo Anda</p>
            <p className="saldo-amount">
              {balance && balance.amount ? balance.amount : 'Saldo tidak tersedia'}
            </p>
            <button onClick={() => navigate('/saldo')}>Lihat Saldo</button>
          </div>
        </section>

        <div className="services-container">
          <div
            className="services-list"
            ref={servicesListRef}
            onClick={(e) => handleScroll(e)}
          >
            {servicesList.map((service, index) => (
              <div key={index} className="service-item">
                <img src={service.icon} alt={service.name} />
                <p>{service.name}</p>
              </div>
            ))}
          </div>
        </div>

        <section className="banner-slider" ref={bannerSliderRef}>
          <div className="banner-list">
            {banner.map((promo, index) => (
              <div
                key={index}
                className={`banner-item ${index === currentBanner ? 'active' : ''}`}
              >
                <img src={promo.imageUrl} alt={`Promo ${index + 1}`} />
              </div>
            ))}
          </div>
        </section>
      </main>

      <footer>
        {/* Footer content here */}
      </footer>
    </div>
  );
};

export default HomePage;
