import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/HomePage.css';
import ProfilePhoto from '../assets/ProfilePhoto.png'; // Fallback image
import PBBIcon from '../assets/PBB.png';
import ListrikIcon from '../assets/Listrik.png';
import PulsaIcon from '../assets/Pulsa.png';
import PDAMIcon from '../assets/PDAM.png';
import PGNIcon from '../assets/PGN.png';
import TelevisiIcon from '../assets/Televisi.png';
import MusikIcon from '../assets/Musik.png';
import VoucherGameIcon from '../assets/Game.png';
import VoucherMakananIcon from '../assets/VoucherMakanan.png';
import KurbanIcon from '../assets/Kurban.png';
import ZakatIcon from '../assets/Zakat.png';
import PaketDataIcon from '../assets/PaketData.png';
import Banner1 from '../assets/Banner1.png';
import Banner2 from '../assets/Banner2.png';
import Banner3 from '../assets/Banner3.png';
import Banner4 from '../assets/Banner4.png';
import Banner5 from '../assets/Banner5.png';
import BackgroundSaldo from '../assets/BackgroundSaldo.png';

const HomePage = () => {
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true); // Loading state
  const [dropdownVisible, setDropdownVisible] = useState(false); // State to control dropdown visibility
  const navigate = useNavigate();
  const [topUpAmount, setTopUpAmount] = useState('');
  const [isTopUpButtonEnabled, setIsTopUpButtonEnabled] = useState(false);

  const handleTopUpAmountChange = (e) => {
    const amount = parseInt(e.target.value.replace(/\D/g, ''), 10); //Remove non-digits
    setTopUpAmount(amount);
    setIsTopUpButtonEnabled(amount >= 10000 && amount <= 1000000);
  };

  const handlePredefinedAmountClick = (amount) => {
    setTopUpAmount(amount);
    setIsTopUpButtonEnabled(true);
  };

  const handleTopUpSubmit = async () => {
    if (isTopUpButtonEnabled) {
      try {
        const response = await fetch('https://take-home-test-api.nutech-integrasi.com/topup', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ amount: topUpAmount }),
        });

        if (!response.ok) {
          console.error('Top up failed:', response);
          // Handle error appropriately (e.g., display an error message)
        } else {
          console.log('Top up successful!');
          // Handle success (e.g., update saldo, display a success message)
        }
      } catch (error) {
        console.error('Top up error:', error);
        // Handle error appropriately
      }
    }
  };

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token'); // Get token from localStorage

      if (!token) {
        setError('Token tidak ditemukan!'); // Show error if no token is found
        setLoading(false);
        return;
      }

      try {
        const response = await fetch('https://take-home-test-api.nutech-integrasi.com/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,  // Use token in header
            'accept': 'application/json',         // Accept JSON response
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          setError(`Terjadi kesalahan: ${errorData.message || 'Unknown error'}`);
        } else {
          const data = await response.json();
          setUserData(data);
        }
      } catch (error) {
        setError('Terjadi kesalahan saat mengambil data user.');
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false); // Stop loading after fetching data
      }
    };

    fetchUserData();
  }, []);

  // Toggle the dropdown visibility
  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);
  };

  // Logout function
  const logout = () => {
    localStorage.removeItem('token');
    navigate('/login'); // Navigate to login page after logout
  };

  // Handle profile update
  const updateProfile = () => {
    navigate('/update-profile'); // Redirect to the profile update page
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!userData) {
    return <div>Data user tidak tersedia.</div>;
  }

  const services = [
    { name: 'PBB', icon: <img src={PBBIcon} alt="PBB Icon" className="service-icon" /> },
    { name: 'Listrik', icon: <img src={ListrikIcon} alt="Listrik Icon" className="service-icon" /> },
    { name: 'Pulsa', icon: <img src={PulsaIcon} alt="Pulsa Icon" className="service-icon" /> },
    { name: 'PDAM', icon: <img src={PDAMIcon} alt="PDAM Icon" className="service-icon" /> },
    { name: 'PGN', icon: <img src={PGNIcon} alt="PGN Icon" className="service-icon" /> },
    { name: 'Televisi', icon: <img src={TelevisiIcon} alt="Televisi Icon" className="service-icon" /> },
    { name: 'Musik', icon: <img src={MusikIcon} alt="Musik Icon" className="service-icon" /> },
    { name: 'Voucher Game', icon: <img src={VoucherGameIcon} alt="VoucherGame Icon" className="service-icon" /> },
    { name: 'Voucher Makanan', icon: <img src={VoucherMakananIcon} alt="VoucherMakanan Icon" className="service-icon" /> },
    { name: 'Kurban', icon: <img src={KurbanIcon} alt="Kurban Icon" className="service-icon" /> },
    { name: 'Zakat', icon: <img src={ZakatIcon} alt="Zakat Icon" className="service-icon" /> },
    { name: 'Paket Data', icon: <img src={PaketDataIcon} alt="PaketData Icon" className="service-icon" /> },
  ];

  const promotions = [
    { image: Banner1 },
    { image: Banner2 },
    { image: Banner3 },
    { image: Banner4 },
    { image: Banner5 },
  ];

  return (
    <div className="homepage">
      <header>
        <h1>SIMS PPOB</h1>
        <nav>
          <ul>
            <li>Top Up</li>
            <li>Transaction</li>
            <li
              className="account-btn"
              onClick={toggleDropdown}
            >
              Akun
              {dropdownVisible && (
                <div className="dropdown-menu">
                  <button onClick={() => navigate('/update-profile')}>Update Profile</button>
                  <button onClick={logout}>Logout</button>
                </div>
              )}
            </li>
          </ul>
        </nav>
      </header>

      <main>
        <section className="user-info">
          <img src={userData.profileImage ? userData.profileImage : ProfilePhoto} alt="User Avatar" className="avatar" />
          <p>Selamat datang, {userData.name}</p>
        </section>

        <section className="saldo">
          <div className="saldo-container">
            <p>Saldo Anda</p>
            <p className="saldo-amount">
              {userData.saldo ? userData.saldo : 'Saldo tidak tersedia'}
            </p>
            <button onClick={() => navigate('/saldo')}>Lihat Saldo</button>
          </div>
        </section>

        <section className="top-up">
          <h2>Nominal Top Up</h2>
          <div className="top-up-input">
            <input
              type="number"
              placeholder="Masukkan nominal"
              value={topUpAmount}
              onChange={handleTopUpAmountChange}
            />
          </div>
          <div className="top-up-buttons">
            {[10000, 20000, 50000, 100000, 250000, 500000].map(amount => (
              <button key={amount} onClick={() => handlePredefinedAmountClick(amount)}>
                Rp{amount.toLocaleString()}
              </button>
            ))}
          </div>
          <button className="top-up-button" onClick={handleTopUpSubmit} disabled={!isTopUpButtonEnabled}>
            Top Up
          </button>
          <p className="top-up-info">
            Minimum: Rp10.000<br />
            Maximum: Rp1.000.000
          </p>
        </section>

        <section className="services">
          {services.map((service, index) => (
            <div key={index} className="service-item">
              {service.icon}
              <p>{service.name}</p>
            </div>
          ))}
        </section>

        <section className="promotions">
          {promotions.map((promo, index) => (
            <div key={index} className="promo-item">
              <img src={promo.image} alt={`Promotion ${index + 1}`} />
            </div>
          ))}
        </section>
      </main>

      <footer>
        {/* Footer content here */}
      </footer>
    </div>
  );
};

export default HomePage;
