import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import InputField from './InputField';
import './styles/LoginForm.css';
import illustrator from './assets/illustrator.png';

const LoginForm = ({ setIsLoggedIn }) => {  // Menerima prop setIsLoggedIn
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
  
    try {
      const response = await fetch('https://take-home-test-api.nutech-integrasi.com/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
  
      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.message || 'Login gagal.');
      } else {
        const data = await response.json();
        console.log('Login berhasil:', data);
        localStorage.setItem('isLoggedIn', 'true');  // Menyimpan status login
        localStorage.setItem('token', data.token);  // Menyimpan token login
        setIsLoggedIn(true); // Set state isLoggedIn di parent component
        navigate('/home');
      }
    } catch (error) {
      setError('Terjadi kesalahan. Silakan coba lagi.');
      console.error('Error:', error);
    }
  };
  

  return (
    <div className="loginContainer">
      <div className="formContainer">
        <div className="loginForm">
          <h1>SIMS PPOB</h1>
          <p>Masuk atau buat akun untuk memulai</p>
          {error && <p className="error">{error}</p>}
          <form onSubmit={handleSubmit}>
            <InputField
              label="Email :"
              type="email"
              placeholder="masukkan email anda"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <InputField
              label="Password :"
              type="password"
              placeholder="masukkan password anda"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit" className="button-red">
              Masuk
            </button>

            <p>
              Belum punya akun? <a href="/register">Registrasi di sini</a>
            </p>
          </form>
        </div>
      </div>
      <div className="illustrationContainer">
        <img src={illustrator} alt="Illustration" className="illustration" />
      </div>
    </div>
  );
};

export default LoginForm;
