// Store.js
import { configureStore } from '@reduxjs/toolkit';
import rootReducer from './reducers'; // Impor rootReducer dari reducers.js

const store = configureStore({
  reducer: rootReducer, // Gabungkan semua reducer di sini
});

export default store;
