import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./styles/TopUpPage.css";

const TopUpPage = () => {
  const [topUpAmount, setTopUpAmount] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleTopUpAmountChange = (e) => {
    setTopUpAmount(e.target.value);
    setError("");
  };

  const handlePredefinedAmountClick = (amount) => {
    setTopUpAmount(amount);
    setError("");
  };

  const handleTopUpSubmit = () => {
    if (!topUpAmount || topUpAmount <= 0) {
      setError("Nominal top up harus lebih dari 0");
      return;
    }
    alert(`Top Up sebesar Rp${Number(topUpAmount).toLocaleString()} berhasil!`);
    navigate("/transaction");
  };

  return (
    <div className="top-up-container">
      <header className="top-up-header">
        <h1>SIMS PPOB</h1>
        <nav>
          <a href="#">Top Up</a>
          <a href="#">Transaction</a>
          <a href="#">Akun</a>
        </nav>
      </header>

      <div className="top-up-content">
        <div className="top-up-balance">
          <h2>Saldo anda</h2>
          <p className="balance-amount">Rp 0</p>
          <button className="hide-balance">Tutup Saldo üëÅ</button>
        </div>

        <div className="user-greeting">
          <h3>Selamat datang,</h3>
          <h2>Kristanto Wibowo</h2>
          <p>Silahkan masukkan</p>
        </div>

        <h3 className="top-up-title">Nominal Top Up</h3>
        {error && <p className="error-message">{error}</p>}

        <div className="top-up-form">
          <input
            type="number"
            placeholder="Masukkan nominal Top Up"
            value={topUpAmount}
            onChange={handleTopUpAmountChange}
          />
          <button className="top-up-button" onClick={handleTopUpSubmit}>
            Top Up
          </button>
        </div>

        <div className="predefined-amounts">
          <button onClick={() => handlePredefinedAmountClick(10000)}>
            Rp10.000
          </button>
          <button onClick={() => handlePredefinedAmountClick(20000)}>
            Rp20.000
          </button>
          <button onClick={() => handlePredefinedAmountClick(50000)}>
            Rp50.000
          </button>
          <button onClick={() => handlePredefinedAmountClick(100000)}>
            Rp100.000
          </button>
          <button onClick={() => handlePredefinedAmountClick(250000)}>
            Rp250.000
          </button>
          <button onClick={() => handlePredefinedAmountClick(500000)}>
            Rp500.000
          </button>
        </div>
      </div>
    </div>
  );
};

export default TopUpPage;
