import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './TopUpPage.css';

const TopUpPage = () => {
  const [topUpAmount, setTopUpAmount] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleTopUpAmountChange = (e) => {
    const amount = parseInt(e.target.value.replace(/\D/g, ''), 10);
    setTopUpAmount(amount);
  };

  const handlePredefinedAmountClick = (amount) => {
    setTopUpAmount(amount);
  };

  const handleTopUpSubmit = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('You are not logged in. Please login.');
      return;
    }

    if (topUpAmount <= 0 || isNaN(topUpAmount)) {
      setError('Please enter a valid amount greater than 0.');
      return;
    }

    try {
      const response = await fetch('https://take-home-test-api.nutech-integrasi.com/topup', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ top_up_amount: topUpAmount }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.message || 'Top up failed.');
      } else {
        const data = await response.json();
        console.log('Top up successful:', data);
        // Update UI, redirect, or other actions after success
        navigate('/home'); // Redirect to home after successful top-up
      }
    } catch (error) {
      setError('An error occurred during top-up.');
      console.error('Error:', error);
    }
  };

  return (
    <div className="top-up-page">
      <h1>Top Up</h1>
      {error && <p className="error-message">{error}</p>}
      <div className="top-up-form">
        <input
          type="number"
          placeholder="Masukkan nominal Top Up"
          value={topUpAmount}
          onChange={handleTopUpAmountChange}
        />
        <div className="predefined-amounts">
          <button onClick={() => handlePredefinedAmountClick(10000)}>Rp10.000</button>
          <button onClick={() => handlePredefinedAmountClick(20000)}>Rp20.000</button>
          <button onClick={() => handlePredefinedAmountClick(50000)}>Rp50.000</button>
          <button onClick={() => handlePredefinedAmountClick(100000)}>Rp100.000</button>
          <button onClick={() => handlePredefinedAmountClick(250000)}>Rp250.000</button>
          <button onClick={() => handlePredefinedAmountClick(500000)}>Rp500.000</button>
        </div>
        <button onClick={handleTopUpSubmit}>Top Up</button>
      </div>
    </div>
  );
};

export default TopUpPage;
