import React, { useState } from 'react';
import './styles/TopUpPage.css'; // Pastikan path sudah benar

function TopUpPage() {
  const [topUpAmount, setTopUpAmount] = useState('');
  const [error, setError] = useState('');

  const handleTopUpAmountChange = (event) => {
    setTopUpAmount(event.target.value);
    setError(''); // Reset error jika ada
  };

  const handleTopUpSubmit = (event) => {
    event.preventDefault();
    if (!topUpAmount || isNaN(topUpAmount)) {
      setError('Please enter a valid amount');
      return;
    }
    alert(`Top-up successful: ${topUpAmount}`);
    setTopUpAmount('');
  };

  const handlePredefinedAmountClick = (amount) => {
    setTopUpAmount(amount.toString());
  };

  return (
    <div className="topup-page">
      <h1>Top-Up Page</h1>
      <form onSubmit={handleTopUpSubmit}>
        <label>
          Enter amount:
          <input
            type="text"
            value={topUpAmount}
            onChange={handleTopUpAmountChange}
            placeholder="Enter amount"
          />
        </label>
        {error && <p className="error-message">{error}</p>}
        <button type="submit">Top Up</button>
      </form>
      <div className="predefined-amounts">
        <p>Or choose a predefined amount:</p>
        <button onClick={() => handlePredefinedAmountClick(50000)}>50,000</button>
        <button onClick={() => handlePredefinedAmountClick(100000)}>100,000</button>
        <button onClick={() => handlePredefinedAmountClick(200000)}>200,000</button>
      </div>
    </div>
  );
}

export default TopUpPage;
