import React from 'react';

const InputField = ({ label, type, placeholder, value, onChange }) => {
  return (
    <div className="input-field">
      {/* Menambahkan htmlFor untuk keterkaitan antara label dan input */}
      <label htmlFor={label}>{label}</label>
      <input
        type={type}
        id={label} // id yang digunakan di input
        placeholder={placeholder}
        value={value} // value yang diterima dari komponen induk
        onChange={(e) => onChange(e.target.value)} // menanggapi perubahan input
      />
    </div>
  );
};

export default InputField;
