import React, { useEffect, useState } from 'react';
import './styles/HomePage.css';

const HomePage = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Retrieve the user data from localStorage
    const storedData = JSON.parse(localStorage.getItem('userData'));
    if (storedData) {
      setUserData(storedData); // Set user data in state
    }
  }, []);

  if (!userData) {
    return <div>Loading...</div>; // Loading state until userData is available
  }

  return (
    <div className="homepage">
      <header>
        <h1>SIMS PPOB</h1>
        <nav>
          <ul>
            <li>Top Up</li>
            <li>Transaction</li>
            <li>Akun</li>
          </ul>
        </nav>
      </header>

      <main>
        <section className="user-info">
          <img src="/path/to/user-avatar.png" alt="User Avatar" />
          <p>Selamat datang, {userData.name}</p> {/* Displaying user's name */}
        </section>

        <section className="saldo">
          <div className="saldo-container">
            <p>Saldo Anda</p>
            <p className="saldo-amount">{userData.saldo}</p> {/* Displaying user's saldo */}
            <button>Lihat Saldo</button>
          </div>
        </section>

        <section className="services">
          {/* services map logic */}
        </section>

        <section className="promotions">
          {/* promotions map logic */}
        </section>
      </main>

      <footer>
        {/* Footer */}
      </footer>
    </div>
  );
};

export default HomePage;
