import React, { useState } from 'react';
import InputField from './InputField';
import './styles/RegisterForm.css';
import illustration from './assets/illustrator.png'; // Sesuaikan path gambar

const RegisterForm = () => {
  // ... (state dan fungsi handleSubmit) ...

  return (
    <div className="registerContainer">
      <div className="formContainer">
        <div> {/* Tambahkan div untuk membungkus formulir */}
          <h1>SIMS PPOB</h1>
          <p>Lengkap data untuk membuat akun</p>
          {error && <p className="error">{error}</p>}
          <form onSubmit={handleSubmit}>
            <InputField label="Email" type="email" placeholder="masukkan email anda" value={email} onChange={(e) => setEmail(e.target.value)} />
            <InputField label="Nama Depan" type="text" placeholder="nama depan" value={firstName} onChange={(e) => setFirstName(e.target.value)} />
            <InputField label="Nama Belakang" type="text" placeholder="nama belakang" value={lastName} onChange={(e) => setLastName(e.target.value)} />
            <InputField label="Buat Password" type="password" placeholder="buat password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <InputField label="Konfirmasi Password" type="password" placeholder="konfirmasi password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
            <button type="submit" className="button-blue">Registrasi</button>
          </form>
          <p>Sudah punya akun? <a href="/">Login di sini</a></p>
        </div>
      </div>
      <div className="illustrationContainer">
        <img src={illustration} alt="Illustration" className="illustration" />
      </div>
    </div>
  );
};

export default RegisterForm;
