import React, { useEffect, useRef, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { setProfile, setBalance, setServices, setBanner, setError } from '../redux/actions';
import './styles/HomePage.css';
import ProfilePhoto from '../assets/ProfilePhoto.png';
// Import service icons
import {
  PBBIcon,
  ListrikIcon,
  PulsaIcon,
  PDAMIcon,
  PGNIcon,
  TelevisiIcon,
  MusikIcon,
  VoucherGameIcon,
  VoucherMakananIcon,
  KurbanIcon,
  ZakatIcon,
  PaketDataIcon
} from '../assets'; // Assuming you have an index file for assets

const HomePage = () => {
  const servicesListRef = useRef();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Redux state
  const profile = useSelector((state) => state.profile);
  const balance = useSelector((state) => state.balance);
  const services = useSelector((state) => state.services);
  const banner = useSelector((state) => state.banner);
  const error = useSelector((state) => state.error);

  // Function to fetch all data
  const fetchData = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      dispatch(setError('Token tidak ditemukan!'));
      return;
    }

    try {
      const responses = await Promise.all([
        fetch('https://take-home-test-api.nutech-integrasi.com/profile', {
          headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' },
        }),
        fetch('https://take-home-test-api.nutech-integrasi.com/balance', {
          headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' },
        }),
        fetch('https://take-home-test-api.nutech-integrasi.com/services', {
          headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' },
        }),
        fetch('https://take-home-test-api.nutech-integrasi.com/banner', {
          headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' },
        }),
      ]);

      const [profileResponse, balanceResponse, servicesResponse, bannerResponse] = responses;

      if (!profileResponse.ok) throw new Error('Profile tidak dapat diambil');
      if (!balanceResponse.ok) throw new Error('Balance tidak dapat diambil');
      if (!servicesResponse.ok) throw new Error('Services tidak dapat diambil');
      if (!bannerResponse.ok) throw new Error('Banner tidak dapat diambil');

      const profileData = await profileResponse.json();
      const balanceData = await balanceResponse.json();
      const servicesData = await servicesResponse.json();
      const bannerData = await bannerResponse.json();

      // Dispatch data to redux
      dispatch(setProfile(profileData));
      dispatch(setBalance(balanceData));
      dispatch(setServices(servicesData));
      dispatch(setBanner(bannerData));

    } catch (error) {
      dispatch(setError(error.message || 'Terjadi kesalahan saat mengambil data.'));
      console.error('Error fetching data:', error.message);
    }
  };

  useEffect(() => {
    fetchData();
    return () => {
      dispatch(setError(null)); // Reset error state on cleanup
    };
  }, [dispatch]);

  // Handle error loading
  if (error) {
    return <div className="error-message">{error}</div>;
  }

  // Handle loading state
  if (!profile) {
    return <div>Loading...</div>;
  }

  const servicesList = [
    { name: 'PBB', icon: PBBIcon },
    { name: 'Listrik', icon: ListrikIcon },
    { name: 'Pulsa', icon: PulsaIcon },
    { name: 'PDAM', icon: PDAMIcon },
    { name: 'PGN', icon: PGNIcon },
    { name: 'Televisi', icon: TelevisiIcon },
    { name: 'Musik', icon: MusikIcon },
    { name: 'Voucher Game', icon: VoucherGameIcon },
    { name: 'Voucher Makanan', icon: VoucherMakananIcon },
    { name: 'Kurban', icon: KurbanIcon },
    { name: 'Zakat', icon: ZakatIcon },
    { name: 'Paket Data', icon: PaketDataIcon },
  ];

  // Scroll right function for services list
  const scrollRight = () => {
    if (servicesListRef.current) {
      servicesListRef.current.scrollBy({
        left: 200,
        behavior: 'smooth',
      });
    }
  };

  return (
    <div className="homepage">
      <header>
        <h1 className="logo">
          <Link to="/" style={{ textDecoration: 'none', color: 'black' }}>
            SIMS PPOB
          </Link>
        </h1>
        <nav>
          <ul>
            <li><Link to="/topup">Top Up</Link></li>
            <li><Link to="/transaction">Transaction</Link></li>
            <li><Link to="/account">Akun</Link></li>
          </ul>
        </nav>
      </header>

      <main>
        <section className="user-info">
          <img src={profile.profileImage || ProfilePhoto} alt="User Avatar" className="avatar" />
          <p>Selamat datang, {profile.name}</p>
        </section>

        <section className="saldo">
          <div className="saldo-container">
            <p>Saldo Anda</p>
            <p className="saldo-amount">
              {balance && balance.amount ? `Rp ${balance.amount}` : 'Saldo tidak tersedia'}
            </p>
            <button onClick={() => navigate('/saldo')}>Lihat Saldo</button>
          </div>
        </section>

        <div className="services-container">
          <div className="services-list" ref={servicesListRef}>
            {servicesList.map((service, index) => (
              <div key={index} className="service-item">
                <img src={service.icon} alt={service.name} />
                <p>{service.name}</p>
              </div>
            ))}
          </div>
          <button onClick={scrollRight} className="scroll-right-btn">Scroll Right</button>
        </div>

        <section className="services">
          {services.length > 0 ? (
            services.map((service, index) => (
              <div key={index} className="service-item">
                <img src={service.icon} alt={`${service.name} Icon`} className="service-icon" />
                <p>{service.name}</p>
              </div>
            ))
          ) : (
            <p>Tidak ada layanan saat ini.</p>
          )}
        </section>

        <section className="promotions">
          {banner.length > 0 ? (
            banner.map((promo, index) => (
              <div key={index} className="promo-item">
                {promo.imageUrl ? (
                  <img src={promo.imageUrl} alt={`Promotion ${index + 1}`} />
                ) : (
                  <p>Image URL tidak tersedia.</p>
                )}
              </div>
            ))
          ) : (
            <p>Tidak ada promo saat ini.</p>
          )}
        </section>
      </main>

      <footer>
        {/* Footer content here */}
        <p>&copy; 2023 SIMS PPOB. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
