import React, { useState } from "react";
import { Link } from "react-router-dom"; // Import Link for navigation
import "./AccountPage.css";

const AccountPage = () => {
  const [formData, setFormData] = useState({
    email: "wallet@nutech.com",
    firstName: "Kristanto",
    lastName: "Wibowo",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form data submitted:", formData);
  };

  return (
    <div className="container">
      <header className="header">
        <h1 className="logo">
          <Link to="/home" className="nav-link">SIMS PPOB</Link> {/* Navigate to HomePage */}
        </h1>
        <nav className="nav">
          <Link to="/topup" className="nav-link">Top Up</Link> {/* Navigate to TopUpPage */}
          <Link to="/transaction" className="nav-link">Transaction</Link>
          <Link to="/account" className="nav-link active">Akun</Link> {/* Stay on AccountPage */}
        </nav>
      </header>
      <main className="main">
        <div className="avatar-container">
          <img
            src="https://via.placeholder.com/100"
            alt="User Avatar"
            className="avatar"
          />
          <button className="edit-button">✏️</button>
        </div>
        <h2 className="username">{formData.firstName} {formData.lastName}</h2>

        <form className="form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              className="form-input"
              readOnly
            />
          </div>
          <div className="form-group">
            <label className="form-label">Nama Depan</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              className="form-input"
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Nama Belakang</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              className="form-input"
              onChange={handleInputChange}
            />
          </div>
          <button type="submit" className="submit-button">
            Simpan
          </button>
        </form>
      </main>
    </div>
  );
};

export default AccountPage;
