import { createSlice } from '@reduxjs/toolkit';

// Initial state for the registration
const initialState = {
  error: null,
  registrationStatus: null,
};

// Create a slice for registration state management
const registrationSlice = createSlice({
  name: 'registration',
  initialState,
  reducers: {
    setError: (state, action) => {
      state.error = action.payload; // Directly mutate the state (Redux Toolkit allows this)
    },
    setRegistrationStatus: (state, action) => {
      state.registrationStatus = action.payload;
    },
  },
});

// Export the actions generated by createSlice
export const { setError, setRegistrationStatus } = registrationSlice.actions;

// Export the reducer to be used in the store
export default registrationSlice.reducer;
