import React, { useState } from 'react';

const transactions = [
  { amount: "+ Rp.10.000", date: "17 Agustus 2023 13:10 WIB", type: 'Top Up Saldo' },
  { amount: "- Rp.40.000", date: "17 Agustus 2023 12:10 WIB", type: 'Pembayaran' },
  { amount: "- Rp.10.000", date: "17 Agustus 2023 11:10 WIB", type: 'Pembayaran' },
  { amount: "+ Rp.50.000", date: "17 Agustus 2023 10:10 WIB", type: 'Top Up Saldo' },
  { amount: "+ Rp.50.000", date: "17 Agustus 2023 10:10 WIB", type: 'Top Up Saldo' },
  // Tambahkan transaksi lainnya di sini...
];

const TransactionPage = () => {
  const [displayedTransactions, setDisplayedTransactions] = useState(transactions.slice(0, 5)); // Menampilkan 5 transaksi pertama secara default
  const [showAll, setShowAll] = useState(false);

  const handleShowMore = () => {
    setShowAll(true);
    setDisplayedTransactions(transactions);
  };

  return (
    <div className="container">
      <div className="sidebar">
        <h3>Menu</h3>
        <ul>
          <li>Top Up Saldo</li>
          <li>Pulsa Prabayar</li>
          <li>Listrik Pascabayar</li>
          {/* Tambahkan item menu lainnya di sini */}
        </ul>
      </div>
      <div className="main">
        <h1>Transaksi</h1>
        <div className="balance">
          <h3>Saldo Anda</h3>
          <span>Rp. <span id="balance">.....</span></span>{' '}
          <a href="#">Lihat Saldo</a>
        </div>

        <div id="transactions">
          {displayedTransactions.map((transaction, index) => (
            <div className="transaction-item" key={index}>
              <span className="amount">{transaction.amount}</span>
              <span className="date">{transaction.date}</span>
              <span className="type">{transaction.type}</span>
            </div>
          ))}
        </div>
        {!showAll && displayedTransactions.length < transactions.length && (
          <button onClick={handleShowMore}>Tampilkan Semua</button>
        )}
      </div>
    </div>
  );
};

export default TransactionPage;
